/*Table structure for table `page` */

DROP TABLE IF EXISTS `page`;

CREATE TABLE `page` (
  `page_id` varchar(8) NOT NULL,
  `page_title` varchar(100) NOT NULL COMMENT '图文标题',
  `page_summary` varchar(100) DEFAULT NULL COMMENT '摘要',
  `page_content` longtext NOT NULL COMMENT '内容',
  `menu_id` varchar(8) NOT NULL COMMENT '外键，对应菜单id',
